package com.osci.kr.itda.common.controller;import com.osci.kr.itda.common.model.ItDaError;import com.osci.kr.itda.common.model.ItDaResult;import org.apache.logging.log4j.LogManager;import org.apache.logging.log4j.Logger;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;public class ItDaBaseController {    protected Logger logger = LogManager.getLogger(this.getClass());    public ResponseEntity<?> ItDaResponse(ItDaResult result){        ItDaError itdaError = new ItDaError();        int code = result.getResultCode();        if (code < 200 || code >= 300){            itdaError.setErrorCode(result.getResultCode());            itdaError.setErrorMsg(result.getResultMsg());            return new ResponseEntity<ItDaError>(itdaError, HttpStatus.valueOf(code));        }        return new ResponseEntity<ItDaResult>(result, HttpStatus.valueOf(code));    }}