package com.osci.kr.itda.common.model;/** * 요청결과 model * @author 박재형 * @since 2020.12.04 * @version 1.0 * @see * * <pre> * << 개정이력(Modification Information) >> * *   수정일      수정자           수정내용 *  -------    --------    --------------------------- *  2020.12.04 박재형         최초 생성 * * </pre> */public class ItDaResult {    public ItDaResult(){        this.setResultCode(200);    }    /**     * 결과 코드     */    private int resultCode = 200;    public int getResultCode() {        return resultCode;    }    public void setResultCode(int resultCode) {        this.resultCode = resultCode;    }    /**     * 에러 메시지     */    private String errorCode = "";    public String getErrorCode() {        return errorCode;    }    public void setErrorCode(String msg) {        this.errorCode = msg;    }    /**     * 결과 메세지     */    private String resultMsg = "";    public String getResultMsg() {        return resultMsg;    }    public void setResultMsg(String resultMsg) {        this.resultMsg = resultMsg;    }    /**     * 결과 데이터     */    private Object resultData = null;    public Object getResultData() {        return resultData;    }    public void setResultData(Object resultData) {        this.resultData = resultData;    }}