package com.osci.kr.itda.controller;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.security.authentication.AuthenticationManager;import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;import org.springframework.security.core.Authentication;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpSession;@Controllerpublic class IndexController{    private final Logger logger = LoggerFactory.getLogger(this.getClass());    @CrossOrigin(origins = "*" ,allowedHeaders = "*")    @GetMapping    ResponseEntity index(HttpServletRequest request, HttpSession session) {        Authentication authentication = SecurityContextHolder.getContext().getAuthentication();        return new ResponseEntity<>(authentication.getPrincipal(), HttpStatus.OK);    }}