package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_comment")@Table(indexes = {@Index(columnList="boarduserid"), @Index(columnList="boardnickname"),                 @Index(columnList="boardnum"),@Index(columnList="cmtuserid"),                 @Index(columnList="cmtcontent") })public class BoardComment extends RepresentationModel<Board> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long cmtid;    @Column(length = 11, nullable = false)    private Long boardnum;    @Column(length = 100, nullable = false)    private String boarduserid;    @Column(length = 255, nullable = false)    private String boardnickname;    @Column(length = 10, nullable = true)    private Long replyidx;    @Column(columnDefinition = "LONGTEXT")    private String cmtcontent;    @Column(length = 100, nullable = true)    private String cmtuserid;    @Column(length = 100, nullable = true)    private String cmtnickname;    @Column(length = 100, nullable = true)    private String cmtuseremail;    @Column(length = 255, nullable = true)    private String photo;    @CreationTimestamp    private LocalDateTime regdateTime;    @CreationTimestamp    private LocalDateTime regUpdateTime;    @Column(length = 11, nullable = true)    private int cmtlike;    @Column(length = 100, nullable = true)    private String cmtregiIp;    @Column(length = 10, nullable = true)    private String ynDel;    @Column(length = 10, nullable = true)    private Boolean statelike;    @Builder    @JsonCreator    public BoardComment(@JsonProperty("boardnum") Long boardnum, @JsonProperty("boarduserid") String boarduserid,                 @JsonProperty("boardnickname") String boardnickname,@JsonProperty("cmtcontent") String cmtcontent,                 @JsonProperty("cmtuserid") String cmtuserid, @JsonProperty("cmtnickname") String cmtnickname,                 @JsonProperty("cmtuseremail") String cmtuseremail, @JsonProperty("regdateTime") LocalDateTime regdateTime,                 @JsonProperty("regUpdateTime") LocalDateTime regUpdateTime, @JsonProperty("cmtlike") int cmtlike,                 @JsonProperty("cmtregiIp") String cmtregiIp, @JsonProperty("ynDel") String ynDel, @JsonProperty("statelike") Boolean statelike,                 @JsonProperty("replyidx") Long replyidx , @JsonProperty("photo") String photo) {        this.boardnum = boardnum;        this.boarduserid = boarduserid;        this.boardnickname = boardnickname;        this.cmtcontent = cmtcontent;        this.cmtuserid = cmtuserid;        this.cmtnickname = cmtnickname;        this.cmtuseremail = cmtuseremail;        this.regdateTime = regdateTime;        this.regUpdateTime = regUpdateTime;        this.cmtlike = 0;        this.cmtregiIp = cmtregiIp;        this.ynDel ="N";        this.statelike = false;        this.replyidx =replyidx;        this.photo = photo;    }}