package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;/** * 댓글 파일업로드 */@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_comment_file")@Table(indexes = {@Index(columnList="userid"), @Index(columnList="boardnum"),@Index(columnList="filename"),@Index(columnList="cmtid")})public class CommentFileupload extends RepresentationModel<CommentFileupload> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long cfid;    @Column(length = 11, nullable = false)    private Long boardnum;    @Column(length = 11, nullable = false)    private Long cmtid;    @Column(length = 100, nullable = false)    private String userid;    @Column(length = 255, nullable = true)    private String originname;    @Column(length = 255, nullable = true)    private String filename;    @Column(length = 11, nullable = true)    private Long filesize;    @Column(length = 100, nullable = true)    private String ext;    @CreationTimestamp    private LocalDateTime regdateTime;    @Column(length = 100, nullable = true)    private String regiIp;    @Column(length = 10, nullable = true)    private String ynDel;    @Column(length = 255, nullable = true)    private String filePath;    @Lob    @Column(name = "imageFile", columnDefinition="BLOB")    private byte[] imageFile;    @Builder    @JsonCreator    public CommentFileupload(@JsonProperty("cfid") Long cfid, @JsonProperty("boardnum") Long boardnum,                      @JsonProperty("cmtid") Long cmtid, @JsonProperty("userid") String userid,                      @JsonProperty("originname") String originname, @JsonProperty("filename") String filename,                      @JsonProperty("filesize") Long filesize, @JsonProperty("ext") String ext,                      @JsonProperty("regdateTime") LocalDateTime regdateTime, @JsonProperty("regiIp") String regiIp,                      @JsonProperty("ynDel") String ynDel , @JsonProperty("filePath") String filePath,                      @JsonProperty("imageFile") byte[] imageFile) {        this.boardnum = boardnum;        this.cmtid = cmtid;        this.userid = userid;        this.originname = originname;        this.filename = filename;        this.filesize = filesize;        this.ext = ext;        this.regdateTime = regdateTime;        this.regiIp = regiIp;        this.regdateTime = regdateTime;        this.ynDel ="N";        this.filePath = filePath;        this.imageFile = imageFile;    }}