package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;/** *  ㅋㅓㅁㅠㄴㅣㅌㅣ ㅌㅔㅇㅣㅂㅡㄹ */@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_com")@Table(indexes = {@Index(columnList="comNm"),@Index(columnList="interCnt") ,@Index(columnList="boardNum")})public class Community extends RepresentationModel<Community> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long comid;    @Column(length = 11, nullable = false)    private Long boardNum;    @Column(length = 255, nullable = false)    private String comNm;    @Column(length = 11, nullable = true)    private int interCnt;    @Column(length = 11, nullable = true)    private int boardCnt;    @Column(length = 11, nullable = true)    private int eventCnt;    @CreationTimestamp    private LocalDateTime regDateTime;    @CreationTimestamp    private LocalDateTime upDateTime;    @Builder    @JsonCreator    public Community(@JsonProperty("comid") Long comid, @JsonProperty("comNm") String comNm,@JsonProperty("boardNum") Long boardNum,                 @JsonProperty("interCnt") int interCnt, @JsonProperty("boardCnt") int boardCnt,                 @JsonProperty("eventCnt") int eventCnt, @JsonProperty("regDateTime") LocalDateTime regDateTime,                     @JsonProperty("upDateTime") LocalDateTime upDateTime) {        this.comid = comid;        this.comNm = comNm;        this.interCnt = 0;        this.boardCnt = 0;        this.eventCnt = 0;        this.regDateTime = regDateTime;        this.upDateTime = upDateTime;        this.boardNum = boardNum;    }}