package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_com_list")@Table(indexes = {@Index(columnList="comNm")})public class CommunityList extends RepresentationModel<CommunityList> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long comlid;    @Column(length = 255, nullable = false)    private String comNm;    @Column(length = 11, nullable = true)    private int comNum;    @Column(length = 100, nullable = true)    private String ynGudok;    @CreationTimestamp    private LocalDateTime regDateTime;    @CreationTimestamp    private LocalDateTime upDateTime;    @Builder    @JsonCreator    public CommunityList(@JsonProperty("comlid") Long comlid, @JsonProperty("comNm") String comNm,@JsonProperty("comNum") int comNum,                         @JsonProperty("regDateTime") LocalDateTime regDateTime,@JsonProperty("ynGudok") String ynGudok,                         @JsonProperty("upDateTime") LocalDateTime upDateTime) {        this.comlid = comlid;        this.comNm = comNm;        this.regDateTime = regDateTime;        this.upDateTime = upDateTime;        this.comNum = comNum;        this.ynGudok = "N";    }}