package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;/** * 게시글 파일업로드 */@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_board_file")@Table(indexes = {@Index(columnList="userid"), @Index(columnList="num"),@Index(columnList="filename")})public class FileUpload extends RepresentationModel<FileUpload> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long pfiid;    @Column(length = 11, nullable = false)    private Long memid;    @Column(length = 100, nullable = false)    private String userid;    @Column(length = 11, nullable = false)    private Long num;    @Column(length = 255, nullable = true)    private String originname;    @Column(length = 255, nullable = true)    private String filename;    @Column(length = 11, nullable = true)    private Long filesize;    @Column(length = 100, nullable = true)    private String ext;    @CreationTimestamp    private LocalDateTime regdateTime;    @Column(length = 100, nullable = true)    private String regiIp;    @Column(length = 10, nullable = true)    private String ynDel;    @Column(length = 255, nullable = true)    private String filePath;    @Lob    @Column(name = "imageFile", columnDefinition="BLOB")    private byte[] imageFile;    @Builder    @JsonCreator    public FileUpload(@JsonProperty("pfiid") Long pfiid, @JsonProperty("memid") Long memid,                     @JsonProperty("userid") String userid, @JsonProperty("num") Long num,                     @JsonProperty("title") String title, @JsonProperty("content") String content,                     @JsonProperty("originname") String originname, @JsonProperty("filename") String filename,                     @JsonProperty("filesize") Long filesize, @JsonProperty("ext") String ext,                     @JsonProperty("regdateTime") LocalDateTime regdateTime, @JsonProperty("regiIp") String regiIp,                     @JsonProperty("ynDel") String ynDel , @JsonProperty("filePath") String filePath,                      @JsonProperty("imageFile") byte[] imageFile) {        this.memid = memid;        this.userid = userid;        this.num = num;        this.originname = originname;        this.filename = filename;        this.filesize = filesize;        this.ext = ext;        this.regdateTime = regdateTime;        this.regiIp = regiIp;        this.regdateTime = regdateTime;        this.ynDel ="N";        this.filePath = filePath;        this.imageFile = imageFile;    }}