package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;/** * 회원관심사 테이블 */@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_interest")@Table(indexes = {@Index(columnList="userid") , @Index(columnList="comNm") , @Index(columnList="comYn")})public class Interest extends RepresentationModel<Interest> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long interid;    @Column(length = 255, nullable = false)    private String userid;    @Column(length = 255, nullable = false)    private String comNm;    @Column(length = 100, nullable = false)    private String comYn;    @Builder    @JsonCreator    public Interest(@JsonProperty("interid") Long interid,@JsonProperty("userid") String userid,                    @JsonProperty("comNm") String comNm,@JsonProperty("comYn") String comYn) {        this.interid = interid;        this.userid = userid;        this.comNm = comNm;        this.comYn = comYn;    }}