package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;/** * 키워드검색 저장 */@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_keyword")@Table(indexes = {@Index(columnList="userid") , @Index(columnList="keyword")})public class KeywordSearch extends RepresentationModel<KeywordSearch> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long sekid;    @Column(length = 255, nullable = false)    private String userid;    @Column(columnDefinition = "TEXT")    private String keyword;    @Column(length = 11, nullable = true)    private int keyworCnt;    @CreationTimestamp    private LocalDateTime regdateTime;    @Column(length = 255, nullable = true)    private String keywordLink;    @Builder    @JsonCreator    public KeywordSearch(@JsonProperty("sekid") Long sekid, @JsonProperty("userid") String userid,                    @JsonProperty("keyword") String keyword, @JsonProperty("keywordLink") String keywordLink,                   @JsonProperty("regdateTime") LocalDateTime regdateTime,@JsonProperty("keyworCnt") int keyworCnt) {        this.sekid = sekid;        this.userid = userid;        this.keyword = keyword;        this.keywordLink = keywordLink;        this.regdateTime = regdateTime;        this.keyworCnt = keyworCnt;    }}