package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;/** * 좋아요 관리 */@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_like_mng")@Table(indexes = {@Index(columnList="userid"), @Index(columnList="numOrcmtid"), @Index(columnList="type")})public class LikeMng extends RepresentationModel<LikeMng> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long likeid;    @Column(length = 255, nullable = false)    private String userid;    @Column(length = 11, nullable = false)    private Long numOrcmtid;    @Column(length = 11, nullable = true)    private int type;    @Column(length = 100, nullable = true)    private boolean islikehit;    @Builder    @JsonCreator    public LikeMng(@JsonProperty("userid") String userid, @JsonProperty("numOrcmtid") Long numOrcmtid,                    @JsonProperty("type") int type, @JsonProperty("islikehit") boolean islikehit) {        this.userid = userid;        this.numOrcmtid = numOrcmtid;        this.type = type;        this.islikehit = islikehit;    }}