package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;/** * 로그인 로그 */@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_member_login_log")@Table(indexes = {@Index(columnList="memid"), @Index(columnList="userid")})public class LoginLog extends RepresentationModel<LoginLog> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long memlogid;    @Column(length = 11, nullable = false)    private Long memid;    @Column(length = 100, nullable = false)    private String userid;    @Column(length = 4, nullable = false)    private int loginSucess;    @CreationTimestamp    private LocalDateTime loginDatetime;    @Column(length = 50, nullable = true)    private String loginIp;    @Column(length = 250, nullable = true)    private String loginReason;    @Builder    @JsonCreator    public LoginLog(@JsonProperty("memlogid") Long memlogid, @JsonProperty("memid") Long memid,                    @JsonProperty("userid") String userid, @JsonProperty("loginSucess") int loginSucess) {        this.memlogid = memlogid;        this.memid = memid;        this.userid = userid;        this.loginSucess = loginSucess;    }}