package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_member_userid")@Table(uniqueConstraints = @UniqueConstraint(columnNames = { "userid"}),        indexes = {@Index(columnList="userid")})public class MemberManageID extends RepresentationModel<MemberManageID> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long mrgid;    @Column(length = 100, nullable = false)    private String userid;    @Column(length = 11, nullable = false)    private int status;    @Builder    @JsonCreator    private MemberManageID(@JsonProperty("mrgid") Long mrgid, @JsonProperty("userid") String userid ,@JsonProperty("status") int status){        this.mrgid =mrgid;        this.userid = userid;        this.status = 0;    }}