package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_mileage")@Table(indexes = {@Index(columnList="userid"), @Index(columnList="mlgCode"),@Index(columnList="regDateTime") })public class Mileage extends RepresentationModel<Mileage> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long milid;    @Column(length = 100, nullable = false)    private String userid;    @Column(length = 100, nullable = true)    private String mlgType;    @Column(length = 100, nullable = true)    private String mlgKind;    @Column(length = 100, nullable = true)    private String mlgRefer;    @Column(length = 100, nullable = true)    private String mlgCode;    @Column(length = 11, nullable = true)    private int point;    @CreationTimestamp    private LocalDateTime regDateTime;    @CreationTimestamp    private LocalDateTime updateDateTime;    @Builder    @JsonCreator    private Mileage(@JsonProperty("milid") Long milid, @JsonProperty("userid") String userid , @JsonProperty("mlgType") String mlgType,                    @JsonProperty("mlgCode") String mlgCode, @JsonProperty("point") int point, @JsonProperty("mlgRefer") String mlgRefer,                    @JsonProperty("regDateTime") LocalDateTime regDateTime , @JsonProperty("mlgKind") String mlgKind,                    @JsonProperty("updateDateTime") LocalDateTime updateDateTime){        this.milid =milid;        this.userid = userid;        this.mlgType =mlgType;        this.mlgCode = mlgCode;        this.point = point;        this.regDateTime = regDateTime;        this.updateDateTime = updateDateTime;        this.mlgKind =mlgKind;        this.mlgRefer = mlgRefer;    }}