package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_noti")@Table(indexes = {@Index(columnList="userid"), @Index(columnList="notiType"),@Index(columnList="targetUserid") })public class Noti extends RepresentationModel<Noti> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long notiid;    @Column(length = 100, nullable = false)    private String userid;    @Column(length = 100, nullable = true)    private String targetUserid;    @Column(length = 100, nullable = true)    private String notiType;    @Column(length = 11, nullable = true)    private Long moveNum;    @Column(length = 255, nullable = true)    private String notiMessage;    @Column(length = 255, nullable = true)    private String notiUrl;    @Column(length = 11, nullable = true)    private int notiCount;    @Column(length = 255, nullable = true)    private String ynRead;    @CreationTimestamp    private LocalDateTime notiDatetime;    @CreationTimestamp    private LocalDateTime notirReaddatetime;    @Builder    @JsonCreator    private Noti(@JsonProperty("notiid") Long notiid, @JsonProperty("userid") String userid , @JsonProperty("targetUserid") String targetUserid,                  @JsonProperty("notiType") String notiType, @JsonProperty("moveNum") Long moveNum,@JsonProperty("notiMessage") String notiMessage,                  @JsonProperty("notiUrl") String notiUrl, @JsonProperty("notiCount") int notiCount,@JsonProperty("ynRead") String ynRead,                  @JsonProperty("notiDatetime") LocalDateTime notiDatetime , @JsonProperty("notirReaddatetime") LocalDateTime notirReaddatetime){        this.notiid =notiid;        this.userid = userid;        this.targetUserid =targetUserid;        this.notiMessage = notiMessage;        this.notiType = notiType;        this.notiCount = notiCount;        this.notiDatetime = notiDatetime;        this.notirReaddatetime = notirReaddatetime;        this.ynRead ="N";        this.moveNum  = moveNum;        this.notiUrl = notiUrl;    }}