package com.osci.kr.itda.entity;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_scrap")@Table(indexes = {@Index(columnList="userid"), @Index(columnList="boardNum") })public class Scrap extends RepresentationModel<Scrap> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long scrid;    @Column(length = 100, nullable = false)    private String userid;    @Column(length = 11, nullable = false)    private Long boardNum;    @Column(length = 10, nullable = true)    private String scrYn;    @Column(length = 255, nullable = true)    private String scrUrl;    @CreationTimestamp    private LocalDateTime regDateTime;    @CreationTimestamp    private LocalDateTime updateDateTime;    @Builder    @JsonCreator    private Scrap(@JsonProperty("scrid") Long scrid, @JsonProperty("userid") String userid , @JsonProperty("boardNum") Long boardNum,                  @JsonProperty("scrYn") String scrYn, @JsonProperty("scrUrl") String scrUrl,                  @JsonProperty("regDateTime") LocalDateTime regDateTime , @JsonProperty("updateDateTime") LocalDateTime updateDateTime){        this.scrid =scrid;        this.userid = userid;        this.boardNum =boardNum;        this.scrYn = scrYn;        this.scrUrl = scrUrl;        this.regDateTime = regDateTime;        this.updateDateTime = updateDateTime;    }}