package com.osci.kr.itda.entity.admin;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import com.osci.kr.itda.entity.Board;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_admin_req")@Table(indexes = {@Index(columnList="adminid"),@Index(columnList="userid") ,@Index(columnList="progressCode"),@Index(columnList="interest")})public class SupportAdmin extends RepresentationModel<SupportAdmin> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long adminid;    @Column(length = 100, nullable = false)    private String userid;    @Column(length = 255, nullable = true)    private String interest;    @Column(length = 255, nullable = true)    private String progress;    @Column(length = 100, nullable = true)    private String progressCode;    @Column(length = 100, nullable = true)    private String userRole;    @Column(length = 255, nullable = true)    private String msg;    @CreationTimestamp    private LocalDateTime regdateTime;    @CreationTimestamp    private LocalDateTime regUpdateTime;    @Builder    @JsonCreator    public SupportAdmin(@JsonProperty("adminid") Long adminid, @JsonProperty("userid") String userid, @JsonProperty("interest") String interest,                     @JsonProperty("progress") String progress, @JsonProperty("progressCode") String progressCode,                     @JsonProperty("userRole") String userRole, @JsonProperty("msg") String msg , @JsonProperty("regdateTime") LocalDateTime regdateTime,                     @JsonProperty("regUpdateTime") LocalDateTime regUpdateTime) {        this.adminid = adminid;        this.userid = userid;        this.interest = interest;        this.progress = progress;        this.progressCode = progressCode;        this.userRole = userRole;        this.msg = msg;        this.regdateTime = regdateTime;    }}