package com.osci.kr.itda.entity.seq;import lombok.Data;import javax.persistence.*;@Entity@Data@SequenceGenerator(        name = "NICKNAME_SEQ_GENERATOR",        sequenceName = "NICKNAME_SEQ",        initialValue = 1,        allocationSize = 1)public class NickName {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE,            generator = "NICKNAME_SEQ_GENERATOR")    private Long id;}