package com.osci.kr.itda.entity.sns;import com.fasterxml.jackson.annotation.JsonCreator;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import org.springframework.hateoas.RepresentationModel;import javax.persistence.*;import java.time.LocalDateTime;@Data@NoArgsConstructor(access = AccessLevel.PUBLIC)@Entity(name="tbl_facebook_user")@Table(uniqueConstraints = @UniqueConstraint(columnNames = {"userid" }),        indexes = {@Index(columnList="userid")})public class FaceBookUser extends RepresentationModel<FaceBookUser> {    @Id    @GeneratedValue(strategy= GenerationType.IDENTITY)    @Column(length = 11, nullable = false)    private Long fbid;    @Column(length = 100, nullable = false)    private String userid;    @Column(length = 255, nullable = true)    private String email;    @Column(columnDefinition = "TEXT", nullable = true)    private String accesstoken;    @Column(length = 255, nullable = true)    private int tokenexpire;    @Column(length = 255, nullable = false)    private String name;    @Column(columnDefinition = "TEXT", nullable = true)    private String picture;    @Column(length = 255, nullable = true)    private String id;    @Column(columnDefinition = "TEXT", nullable = true)    private String signedreq;    @CreationTimestamp    private LocalDateTime registerdatetime;    @UpdateTimestamp    private LocalDateTime registerUpdatetime;    @Builder    @JsonCreator    private FaceBookUser(@JsonProperty("memid") Long fbid, @JsonProperty("userid") String userid , @JsonProperty("email") String email                       , @JsonProperty("accesstoken") String accesstoken , @JsonProperty("tokenexpire") int tokenexpire , @JsonProperty("name") String name                       , @JsonProperty("picture") String picture , @JsonProperty("id") String id , @JsonProperty("signedreq") String signedreq                       , @JsonProperty("registerdatetime") LocalDateTime registerdatetime,@JsonProperty("registerUpdatetime") LocalDateTime registerUpdatetime){        this.fbid = fbid;        this.userid = userid;        this.email = email;        this.userid = userid;        this.accesstoken = accesstoken;        this.tokenexpire = tokenexpire;        this.name = name;        this.picture = picture;        this.id = id;        this.signedreq = signedreq;        this.registerdatetime = registerdatetime;        this.registerUpdatetime = registerUpdatetime;    }}