package com.osci.kr.itda.interceptor;import com.osci.kr.itda.jwt.JwtTokenProvider;import org.springframework.http.HttpHeaders;import org.springframework.http.HttpRequest;import org.springframework.http.client.ClientHttpRequestExecution;import org.springframework.http.client.ClientHttpRequestInterceptor;import org.springframework.http.client.ClientHttpResponse;import org.springframework.web.servlet.HandlerInterceptor;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;public class JwtAuthInterceptor implements ClientHttpRequestInterceptor {    @Override    public ClientHttpResponse intercept(HttpRequest request, byte[] body, ClientHttpRequestExecution execution) throws IOException {        HttpHeaders token = request.getHeaders();        request.getHeaders().add(JwtTokenProvider.ACCESS_TOKEN_NAME, String.valueOf(token.get("accessToken")));        return execution.execute(request, body);    }}