package com.osci.kr.itda.repo;import com.osci.kr.itda.entity.Board;import com.osci.kr.itda.entity.BoardComment;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import java.util.List;import java.util.Optional;public interface BoardCommentRepo extends JpaRepository<BoardComment, Long> {    List<BoardComment> findByBoardnumAndYnDelOrderByRegdateTimeAsc(Long num,String yndel);    Optional<BoardComment> findByCmtid(Long cmtid);    Optional<BoardComment> findByCmtidAndYnDel(Long cmtid,String yndel);    List<BoardComment> findByCmtcontentLikeAndYnDelOrderByRegdateTimeDesc(String word,String yndel);    List<BoardComment> findByReplyidxAndYnDel(Long cmtid,String yndel);    List<BoardComment> findByCmtuseridAndYnDel(String userid,String yndel);    Page<BoardComment> findByCmtuseridAndYnDel(String userid, String yndel, Pageable paging);    List<BoardComment> findByBoardnum(Long num);}