package com.osci.kr.itda.repo;import com.osci.kr.itda.entity.Board;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import java.util.Collection;import java.util.List;import java.util.Optional;public interface BoardRepo extends JpaRepository<Board, Long> {    Page<Board> findByUserid(String userid, Pageable paging);    Page<Board> findByRegtypeAndYnDelAndFlagIn(String regtype, String yndel, List<String> flag, Pageable paging);    @Query(value = "select * from tbl_board b where b.nickname like %?1%", nativeQuery = true)    List<Board> findByNickname(String username);    Optional<Board> findByNum(Long num);    Optional<Board> findByNumAndYnDel(Long num,String yndel);    long deleteAllByUseridAndNum(String userid, long num);    List<Board> findTop2ByUseridAndYnDelOrderByRegdateTimeDesc(String username,String yndel);    List<Board> findByContentLikeOrTitleLikeAndYnDelOrderByRegdateTimeDesc(String content,String title,String yndel);    List<Board> findByUseridAndYnDel(String username,String yndel);    Page<Board> findByUseridAndYnDel(String username,String yndel, Pageable paging);    Board findByUserid(String userid);    Page<Board> findByUseridAndYnDelAndFlagIn(String username,String yndel,List<String> flag, Pageable paging);}