package com.osci.kr.itda.serviceimpl;import com.osci.kr.itda.repo.MembersRepo;import lombok.RequiredArgsConstructor;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;@RequiredArgsConstructor@Servicepublic class CustomUserDetailService implements UserDetailsService {    private  final MembersRepo membersRepo;    @Override    public UserDetails loadUserByUsername(String id) throws UsernameNotFoundException {        return membersRepo.findByUserid(id)                .orElseThrow(() -> new UsernameNotFoundException("사용자를 찾을 수 없습니다."));    }}